import { findAllByText } from '@testing-library/react'
import React, { useState } from 'react'

// Header
const Title = ({title}) => {
  return (
      <h1>{title}</h1>
  )
}

// Button
const Button = ({ text, onClick }) => (
  <button onClick={onClick}>
    {text}
  </button>
)

// Sum
const Sum = ({text, num}) => {
  return(
      <p>{text}: {num}</p>
  )
}

// Average
const Average = ({text, good, bad, all}) => {
  if (all === 0) {
    return (
      <p>{text} no feedback</p>
    )
  }
  return (
    <p>{text} {(good - bad) / all}</p>
  )
}

// Percent
const Percent = ({text, good, all}) => {
  if(all === 0){
    return (
      <p>{text} no feedback</p>
    )
  }
  return (
    <p>{text} {((good * 100) / all)} %</p>
  )
}

// App
const App = () => {
  const mainTitle = 'give feedback'
  const title = 'statistics'

  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0)

  const onClickGood = () => {     // increases good an total feedback
    setGood(good + 1)
    setAll(all + 1)
  }

  const onClickNeutral = () => {  // increases neutral an total feedback
    setNeutral(neutral + 1)
    setAll(all + 1)
  }

  const onClickBad = () => {      // increases bad an total feedback
    setBad(bad + 1)
    setAll(all + 1)
  }

  return (
    <div>
      <Title title={mainTitle} />
      <Button text='good' onClick={onClickGood} />
      <Button text='neutral' onClick={onClickNeutral} />
      <Button text='bad' onClick={onClickBad} />

      <Title title={title} />
      <Sum text='good' num={good}/>
      <Sum text='neutral' num={neutral} />
      <Sum text='bad' num={bad} />
      <Sum text='all' num={all} />
      <Average text='average' good={good} bad={bad} all={all} />
      <Percent text='positive' good={good} all={all}/>
    </div>

  )
}

export default App
