import React, { useState } from 'react'

// Header
const Title = ({title}) => {
  return (
      <h1>{title}</h1>
  )
}

// Button
const Button = ({ text, onClick }) => (
  <button onClick={onClick}>
    {text}
  </button>
)

const Statistics = ({good, bad, neutral}) => {
  var all = good + neutral + bad

  if (all === 0) {
    return (
      <p>No feedback given</p>
    )
  }

  return(
  <>
    <StatisticLine text='neutral' num={neutral} />
    <StatisticLine text='bad' num={bad} />
    <StatisticLine text='all' num={all} />
    <StatisticLine text='average' value={((good - bad) / all)} />
    <Percent text='positive' num={good} total={all} />
  </>
  )
}

// Sum
const Sum = ({text, num}) => {
  return(
      <p>{text} {num}</p>
  )
}

// Average
const StatisticLine = ({ text, value}) => {
  return (
    <p>{text} {value}}</p>
  )
}

// Percent
const Percent = ({text, value, total}) => {
  return (
    <p>{text} {((value * 100) / total)} %</p>
  )
}

// App
const App = () => {
  const mainTitle = 'give feedback'
  const title = 'statistics'

  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <Title title={mainTitle} />
      <Button text='good' onClick={() => setGood(good + 1)} />
      <Button text='neutral' onClick={() => setNeutral(neutral + 1)} />
      <Button text='bad' onClick={() => setBad(bad+1)} />

      <Title title={title} />
      <Statistics good={good} neutral={neutral} bad={bad} />
    </div>

  )
}

export default App
