import React, { useState } from 'react'

// Header
const Title = ({title}) => {
  return (
      <h1>{title}</h1>
  )
}

// Button
const Button = ({ text, onClick }) => (
  <button onClick={onClick}>
    {text}
  </button>
)

// Total
const Sum = ({text, num}) => {
  return(
      <p>{text} {num}</p>
  )
}

// App
const App = () => {
  const mainTitle = 'give feedback'
  const title = 'statistics'

  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const all = 0
  // const average = 0
  // const positive = 100

  const onClickGood = () => {
    setGood(good + 1)
    updateStatistics()
    console.log({all})
  }

  const onClickNeutral = () => {
    setNeutral(neutral + 1)
    updateStatistics()
  }

  const onClickBad = () => {
    setBad(bad + 1)
    updateStatistics()
  }

  const updateStatistics = () => {
    all = good + neutral + bad
  }

  return (
    <div>
      <Title title={mainTitle} />
      <Button text='good' onClick={onClickGood} />
      <Button text='neutral' onClick={onClickNeutral} />
      <Button text='bad' onClick={onClickBad} />

      <Title title={title} />
      <Sum text='good' num={good}/>
      <Sum text='neutral' num={neutral} />
      <Sum text='bad' num={bad} />
    </div>
  )
}

export default App
